import React from "react";
import { useTheme } from "./useTheme";

function App() {
  const { theme, toggleTheme } = useTheme();

  return (
    <div className={`app ${theme}`}>
      {/* Sidebar Navigation */}
      <div className="sidebar">
        <h2>Navigation</h2>
        <a href="#about">About</a>
        <a href="#contact">Contact</a>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <h1>Welcome to the {theme} theme!</h1>
        <button className="toggle-btn" onClick={toggleTheme}>
          Toggle Theme
        </button>

        {/* About Section */}
        <div className="about-box" id="about">
          <h2>About Page</h2>
          <p>
            Hello! I'm Pooja, a software developer passionate about building modern web
            applications. I have expertise in Java, React, SQL, and more. Always eager
            to learn and create efficient solutions!
          </p>
        </div>

        {/* Contact Section */}
        <div className="contact-info" id="contact">
          <h2>Contact Me</h2>
          <p>Email: <a href="mailto:poojasiva615@gmail.com">poojasiva615@gmail.com</a></p>
          <p>üìç Location: Chennai</p>
          <p>üîó LinkedIn: <a href="#">View Profile</a></p>
          <p>üöÄ GitHub: <a href="#">Check My Projects</a></p>
        </div>
      </div>
    </div>
  );
}

export default App;
